import React, { Component, useState } from "react";
import Cookies from "universal-cookie";
import { BrowserRouter as Router, Link } from "react-router-dom";
import Logo from "../img/logo-removebg-preview.png";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const cookies = new Cookies();

  function emailChangeHandler(event) {
    let value = event.target.value;
    setEmail(value);
  }
  function passwordChangeHandler(event) {
    let value = event.target.value;
    setPassword(value);
  }

  function signInSubmitHandler(event) {
    event.preventDefault();

    fetch("https://185.50.193.81:5001/api/customer/signin", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email: email,
        password: password
      })
    })
      .then(response => response.json())
      .then(responseJSON => {
        console.log(responseJSON);
      });
    let jsonString =
      '{"key": "6acd983a-6841-11ea-b708-000c291c2166","expirationDate": "2020-03-17T12:36:16"}';

    var jsonObj = JSON.parse(jsonString);

    console.log("JSON: ", jsonObj);

    cookies.set("sessionKey", jsonObj.key, { path: "/" });
    cookies.set("expirationDate", jsonObj.expirationDate, { path: "/" });
    //window.location.reload();
  }

  return (
    <div className="center-div">
      <div className="row">
        <div className="col col-12">
          <img src={Logo}></img>
        </div>
      </div>

      <div className="row">
        <div className="col col-12">
          <span id="email" className="input-label">
            EMAIL
          </span>
        </div>
      </div>
      <div className="row">
        <div className="col col-12">
          <input
            type="text"
            id="email"
            className="w-100"
            onChange={emailChangeHandler}
          ></input>
        </div>
      </div>
      <div className="m-4"></div>

      <div className="row">
        <div className="col col-12">
          <span id="password" className="input-label">
            KODEORD
          </span>
        </div>
      </div>
      <div className="row">
        <div className="col col-12">
          <input
            type="password"
            id="password"
            className="w-100"
            onChange={passwordChangeHandler}
          ></input>
        </div>
      </div>
      <div className="m-4"></div>
      <div className="row">
        <div className="col col-8">
          <span style={{ fontSize: 13 }}>
            Dont have an account? <Link to="/sign-up">Sign Up</Link>
          </span>
        </div>
        <div className="col col-4">
          <a onClick={signInSubmitHandler} className="btn btn-purple">
            LOGIN
          </a>
        </div>
      </div>
    </div>
  );
}
