Highcharts.chart("container-speed", {
  chart: {
    type: "solidgauge",
    backgroundColor: "#0000"
  },
  tooltip: {
    enabled: false
  },
  title: {
    text: "Current watt usage",
    align: "center",
    style: {
      padding: "10px 0",
      "font-size": "20px",
      "font-weight": "400",
      color: "#03D9F0",
      "line-height": "16px"
    }
  },
  credits: {
    enabled: false
  },
  pane: {
    size: "100%",
    startAngle: -120,
    endAngle: 120,
    background: {
      innerRadius: "80%",
      outerRadius: "100%",
      backgroundColor: "#1A4A8D",
      borderWidth: 0,
      shape: "arc"
    }
  },
  plotOptions: {
    solidgauge: {
      innerRadius: "80%",
      radius: "100%",
      dataLabels: {
        enabled: true,
        useHTML: true,
        borderWidth: 0,
        zIndex: 1000 //added zindex
      }
    }
  },
  yAxis: {
    labels: {
      enabled: false
    },
    stops: [
      [
        0.5,
        {
          linearGradient: {
            x1: 0,
            x2: 0,
            y1: 0,
            y2: 1
          },
          stops: [
            [0, "#03D9F0"] /*03D9F0*/,
            [1, "#06A1D9"] /*06A1D9*/
          ]
        }
      ],
      [
        0.9,
        {
          linearGradient: {
            x1: 0,
            x2: 1,
            y1: 0,
            y2: 0
          },
          stops: [
            [0, "#06A1D9"],
            [1, "#03D9F0"]
          ]
        }
      ]
    ],
    min: 0,
    max: 800,
    gridLineColor: "transparent",
    lineColor: "transparent",
    minorTickLength: 0,
    tickPositions: [650],
    tickColor: "#000000",
    tickPosition: "inside",
    tickLength: 25,
    tickWidth: 3,
    zIndex: 100 //added zindex
  },
  series: [
    {
      data: [390],
      dataLabels: {
        format:
          '<span style="font-size:30px;  color: white">{y} W</span><br />',
        y: -40
      }
    }
  ],
  navigation: {
    buttonOptions: {
      enabled: false
    }
  }
});
// Bring life to the dials
setInterval(function() {
  // Speed
  var chart = $("#container-speed").highcharts(),
    point,
    tickPos,
    newVal,
    inc;

  if (chart) {
    point = chart.series[0].points[0];
    inc = Math.round((Math.random() - 0.5) * 100);
    newVal = point.y + inc;

    if (newVal < 0 || newVal > 800) {
      newVal = point.y - inc;
    }

    point.update(newVal);
  }
}, 500);
