import React, { Component } from "react";
import Cookies from "universal-cookie";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from "../Sites/home";
import Details from "../Sites/details";
import Logo from "../img/logo-removebg-preview.png";
import $ from "jquery";

export default function LoginSite() {
  const cookies = new Cookies();

  function checkSessionKey() {
    fetch("http://185.50.193.81:5001/api/customer/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        //email: email,
        //password: password
      })
    }).then(response => {
      console.log(response);
      cookies.set("sessionKey", response.key, { path: "/" });
      cookies.set("expirationDate", response.expirationDate, {
        path: "/"
      });
    });
  }

  if (cookies.get("sessionKey") === undefined) {
    window.location.href = "/login";
  } else {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="side-bar">
            <div className="row">
              <div className="col col-12">
                <img src={Logo}></img>
              </div>
            </div>
            <div className="row">
              <div className="col col-12 menu">
                <ul>
                  <li>
                    <a onClick={redirect.bind(this)}>Forside</a>
                  </li>
                  <li>
                    <a onClick={redirect.bind(this)}>Detaljer</a>
                  </li>
                  <li>
                    <a onClick={signout}>Log Ud</a>
                  </li>
                </ul>
              </div>
            </div>
            <div className="row">
              <div className="col col-12">
                <a onClick={signout} className="btn btn-purple">
                  LOG UD
                </a>
              </div>
            </div>
          </div>
          <div className="col">
            <Router>
              <Switch>
                <Route path="/details">
                  <Details />
                </Route>
                <Route path="/">
                  <Home />
                </Route>
              </Switch>
            </Router>
          </div>
        </div>
      </div>
    );
  }

  function signout() {
    cookies.remove("sessionKey");
    cookies.remove("expirationDate");
    window.location.href = "/login";
  }

  function redirect(url) {
    this.addClass("active");
    window.location.href = url;
  }

  $("a").click(function() {
    alert("The paragraph was clicked.");
    $(".menu ul li a").removeClass("active");
    this.addClass("active");
  });
}
