import React, { Component } from "react";
import Cookies from "universal-cookie";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from "../Sites/home";

export default function LoginSite() {
  const cookies = new Cookies();

  function checkSessionKey() {
    fetch("http://185.50.193.81:5001/api/customer/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        //email: email,
        //password: password
      })
    }).then(response => {
      console.log(response);
      cookies.set("sessionKey", response.key, { path: "/" });
      cookies.set("expirationDate", response.expirationDate, {
        path: "/"
      });
    });
  }

  function signout() {
    cookies.remove("sessionKey");
    cookies.remove("expirationDate");
  }

  if (cookies.get("sessionKey") === undefined) {
    window.location.href = "/login";
  } else {
    return (
      <div className="container">
        <a onClick={signout} className="btn btn-purple">
          LOG UD
        </a>
        <div className="row">
          <div className="col col-12">
            <Router>
              <Switch>
                <Route path="/">
                  <Home />
                </Route>
              </Switch>
            </Router>
          </div>
        </div>
      </div>
    );
  }
}
